import React from 'react';
import { connect } from 'react-redux';
import { deleteDifferential } from '../../Redux/actions/authActions';

class DifferentialDisplay extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      edit: false
    }
  }

  render(){

    const differentials = this.props.golfer.differentials;

    let differentialList;
    if(differentials){
      differentialList = (differentials.map(differential => (
        <ul id="differentialList">
          <li
            key={differentials.indexOf(differential)}
            onClick={() => this.props.deleteDifferential()}
          >
            On {differential.date}, {differential.differential}
          </li>
        </ul>)));
    }
    else{
      differentialList = null;
    }

    let deleteMessage;
    if(this.state.edit){
      deleteMessage = (<h2>Click on a handicap to delete it</h2>);
    }
    else{
      deleteMessage = null;
    }

    return(
      <div>
        <h2> Your differentials: </h2>
        {differentialList}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { deleteDifferentials }
)(DifferentialDisplay);
