import React from 'react';
import '../../App.css';

function CreateList(props){
  if(props.friends){
  //Lists all the golfer's friends
    const friends = props.friends.map(friend => (
      <ul>
        <li
          key={props.friends.username}
        >
          {friend.username}
        </li>
      </ul>
    ));
    return friends
  }
  else{
    return null;
  }
}

function ListContainer(props){

  const random = [];
  const friends = props.golfer.friends;
  const friendsLength = friends.length();
  const listLenegth = 5;

  if(friendsLength > 6){
    while(listLength--){
      const j = Math.floor(Math.random()*(friendsLength+1));
      random.push(friends[j])
      friends.splice(j, 1);
    }
    <CreateList friends={random}/>
  }
  else{
    <CreateList friends={friends}/>
  }
}


class ReducedFriendList extends React.Component {
  constructor(props){
    super(props);
  }

  render(){
    return(
      <div>
        <h3>Your friends include:</h3>
        <ListContainer id="reducedFriendsList" golfer={this.props.golfer}/>
      </div>
    )
  }
}

export default ReducedFriendList;
