import React from 'react';
import { connect } from 'react-redux';
import { deleteDifferential } from '../../Redux/actions/authActions';

function CreateList(props){

}

class DifferentialDisplay extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      edit: false
    }
    this.editToggle = this.editToggle.bind(this);
  }

  editToggle(){
    this.setState((state, props) => ({
      edit: !state.edit
    }));
  }

  render(){

    const differentials = this.props.golfer.differentials;

    let differentialList;
    if(differentials){
      differentialList = (differentials.map(differential => (
        <ul id="differentialList">
          <li
            key={differentials.indexOf(differential)}
            disabled={!this.state.edit}
            onClick={() => this.props.deleteDifferential(differential)}>
            On {differential.date}, {differential.differential}
          </li>
        </ul>)));
    }
    else{
      differentialList = null;
    }

    let deleteMessage;
    if(this.state.edit){
      deleteMessage = (<h4>Click on a handicap to delete it</h4>);
    }
    else{
      deleteMessage = null;
    }

    let editButton;
    if(this.state.edit){
      editButton = (<button onClick={this.editToggle()}>Edit</button>)
    }
    else{
      editButton = (<button onClick={this.editToggle()}>Stop Editing</button>);
    }

    return(
      <div>
        <h2> Your differentials: </h2>
        {editButton}
        {deleteMessage}
        {differentialList}
      </div>
    );
  }
}

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(
  mapStateToProps,
  { deleteDifferential }
)(DifferentialDisplay);
