import React from 'react';
import data from '../../golfers';
import '../../App.css';
import axios from "axios";

class FriendDetails extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      golfer: null
    }
  }

  componentDidMount(){

    axios.get("Golfers/" + this.props.selected)
    .then(res => {
      console.log("res: ", res);

      this.setState({
        golfer: res.data
      })
    });
  }

  render(){


    return(
      <div>
        <h3>Hi {this.props.selected}!</h3>
      </div>
    )
  }
}

export default FriendDetails;
