class DifferentialDisplay extends React.Component {

  constructor(props){
    super(props);
    this.state = {
      edit: false
    }
  }

  /*
  CreateList(props){


    //Sorting differentials by date
    const unsortedDifferentials = props.differentials;
    const sortedDifferentials = unsortedDifferentials.sort((a,b) =>
    (a.date > b.date) ? 1 : ((b.date > a.date) ? -1 : 0));

    console.log("sortedDifferentials: ", sortedDifferentials);

    //Displaying differentials as an unordered list
    const differentials = props.differentials.map(differential => (
      <ul id="differentialList">
        <li
          key={props.differentials.indexOf(differential)}
        >
          On {differential.date}, {differential.differential}
        </li>
      </ul>
    ));
    if(differentials){
      return differentials
    }
    else{
      return null;
    }
    */

  render(){

    const differentials = this.props.golfer.differentials;

    let differentialList;
    if(differentials){
      differentialList = (differentials.map(differential => (
        <ul id="differentialList">
          <li
            key={differentials.indexOf(differential)}
          >
            On {differential.date}, {differential.differential}
          </li>
        </ul>)));
    }
    else{
      differentialList = null;
    }

    let deleteMessage;
    if(this.state.edit){
      deleteMessage = (<h2>Click on a handicap to delete it</h2>);
    }
    else{
      deleteMessage = null;
    }

    return(
      <div>
        <h2> Your differentials: </h2>

        {differentialList}
      </div>
    );
  }
}

export default DifferentialDisplay;
